function App() {
  const [form, setForm] = useState({
    name: '',
    email: ''
  })

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    })
  }

  return (
    <div>
      <input 
        name="name"
        value={form.name}
        onChange={handleChange}
      />
      <input 
        name="email"
        value={form.email}
        onChange={handleChange}
      />
      <p>{form.name} - {form.email}</p>
    </div>
  )
}

render(<App />)

