function App() {
  const [products] = useState([
    { id: 1, name: 'æ‰‹æœº', price: 3999 },
    { id: 2, name: 'ç”µè„‘', price: 5999 },
    { id: 3, name: 'å¹³æ¿', price: 2999 }
  ])
  const [sortOrder, setSortOrder] = useState('asc')

  const getSortedProducts = () => {
    return [...products].sort((a, b) => {
      if (sortOrder === 'asc') {
        return a.price - b.price
      } else {
        return b.price - a.price
      }
    })
  }

  return (
    <div>
      <button onClick={() => setSortOrder('asc')}>å‡åº</button>
      <button onClick={() => setSortOrder('desc')}>é™åº</button>
      <ul>
        {getSortedProducts().map(product => (
          <li key={product.id}>
            {product.name} - Â¥{product.price}
          </li>
        ))}
      </ul>
    </div>
  )
}

